import * as React from "react";
import { useState } from 'react';
import { Grid, Card, CardMedia, CardContent, Typography, Container, ImageList, ImageListItem, ImageListItemBar, IconButton, Box, Modal} from '@mui/material';

const Result = () => {
    const [selectedImage, setSelectedImage] = useState(null);

    const handleImageClick = (image) => {
        setSelectedImage(image);
    };

    const handleCloseModal = () => {
        setSelectedImage(null);
    };

    const itemData = apiResult.map((imageLink, index) => ({
      img: imageLink,
      title: 'Image ${index + 1}',
    }))

    const showResults = itemData.map((item, index) => {
        return (
            <Box
                key={index}
                sx={{
                    border: "4px solid",
                    borderColor: "white",
                    pointerEvents: "all",
                    cursor: "pointer",
                }}
                onClick={() => handleImageClick(item.img)}
            >
                <ImageListItem>
                    <img
                        srcSet={`${item.img}?w=248&fit=crop&auto=format&dpr=2 2x`}
                        src={`${item.img}?w=248&fit=crop&auto=format`}
                        alt={item.title}
                        loading="lazy"
                        style={{
                          objectFit: 'cover',
                          maxWidth: '100%',
                          maxHeight: '100%',
                        }}
                    />
                    <ImageListItemBar
                        title={item.title}
                        style={{
                          opacity: "1",
                          color: "white",
                          fontSize: '100px'
                        }}
                    />
                </ImageListItem>
                
                
            </Box>
        );
    });

    return (
        <Container style={{ padding: "20px" }} maxWidth={true}>
            <ImageList cols={6} rowHeight={200} sx={{ overflow: "hidden"}}>
                {showResults}
            </ImageList>

            {/* Modal hiển thị ảnh phóng to */}
            <Modal
                open={selectedImage !== null}
                onClose={handleCloseModal}
                aria-labelledby="modal-modal-title"
                aria-describedby="modal-modal-description"
            >
                <div>
                    <Box
                        sx={{
                            position: 'absolute',
                            top: '50%',
                            left: '50%',
                            transform: 'translate(-50%, -50%)',
                        }}
                    >
                        <Typography variant="h6" style={{ position: 'absolute', top: '0', left: '0', color: 'white', padding: '10px' }}>
                            {itemData.find(item => item.img === selectedImage)?.title}
                        </Typography>
                        <img
                            src={selectedImage}
                            alt="Selected Image"
                            style={{ maxWidth: '100%', maxHeight: '100%' }}
                        />
                    </Box>
                </div>
            </Modal>
        </Container>
    );
};


