import * as React from "react";
import { Grid, Card, CardMedia, CardContent, Typography, Container, ImageList, ImageListItem, ImageListItemBar, IconButton, Box} from '@mui/material';


const Result = () => {
    const [selectedImage, setSelectedImage] = React.useState(null);
    const handleImageClick = (image) => {
      setSelectedImage(image);
    };

    const handleCloseClick = () => {
      setSelectedImage(null);
    }


    var showResults = itemData.map((item, index) => {
        return (
            <Box 
                sx={{
                    boder: "4px solid",
                    borderColor: "white",
                    pointerEvents: "all",
                    cursor: "pointer"
                }}

                onClick={() => handleImageClick(item.img)}
            >
                <ImageListItem>
                    <img
                        srcSet={`${item.img}?w=248&fit=crop&auto=format&dpr=2 2x`}
                        src={`${item.img}?w=248&fit=crop&auto=format`}
                        alt={item.title}
                        loading="lazy"
                    />
                    <ImageListItemBar
                        title={item.title}
                        position="below"
                    />
                </ImageListItem>

                <Modal
                  open={selectedImage !== null}
                  onClose={handleCloseClick}
                  aria-labelledby='modal-modal-title'
                  aria-describedby='modal-modal-description'
                >
                  <img
                    src={selectedImage}
                    alt="Selected Image"
                    style={{ maxWidth: '100%', maxHeight: '100%'}}
                  />
                  <div>
                    <Box
                      sx={{
                        position: 'absolute',
                        top: '50%',
                        left: '50%',
                        transform: 'translate(-50%, -50%)',
                      }}
                    >
                    </Box>
                  </div>
                </Modal>
    
            </Box>
        )
    });
    return (
        <Container style={{ padding: "20px" }} maxWidth={true}>
            <ImageList cols={7} rowHeight={150}>
                {showResults}
            </ImageList>
        </Container>
    );
};


const itemData = [
    {
      img: 'https://images.unsplash.com/photo-1551963831-b3b1ca40c98e',
      title: 'Breakfast',
      author: '@bkristastucchio',
      featured: true,
    },
    {
      img: 'https://images.unsplash.com/photo-1551782450-a2132b4ba21d',
      title: 'Burger',
      author: '@rollelflex_graphy726',
    },
    {
      img: 'https://images.unsplash.com/photo-1522770179533-24471fcdba45',
      title: 'Camera',
      author: '@helloimnik',
    },
    {
      img: 'https://images.unsplash.com/photo-1444418776041-9c7e33cc5a9c',
      title: 'Coffee',
      author: '@nolanissac',
    },
    {
      img: 'https://images.unsplash.com/photo-1533827432537-70133748f5c8',
      title: 'Hats',
      author: '@hjrc33',
    },
    {
      img: 'https://images.unsplash.com/photo-1558642452-9d2a7deb7f62',
      title: 'Honey',
      author: '@arwinneil',
      featured: true,
    },
    {
      img: 'https://images.unsplash.com/photo-1516802273409-68526ee1bdd6',
      title: 'Basketball',
      author: '@tjdragotta',
    },
    {
      img: 'https://images.unsplash.com/photo-1518756131217-31eb79b20e8f',
      title: 'Fern',
      author: '@katie_wasserman',
    },
    {
      img: 'https://images.unsplash.com/photo-1597645587822-e99fa5d45d25',
      title: 'Mushrooms',
      author: '@silverdalex',
    },
    {
      img: 'https://images.unsplash.com/photo-1567306301408-9b74779a11af',
      title: 'Tomato basil',
      author: '@shelleypauls',
    },
    {
      img: 'https://images.unsplash.com/photo-1471357674240-e1a485acb3e1',
      title: 'Sea star',
      author: '@peterlaster',
    },
    {
      img: 'https://images.unsplash.com/photo-1589118949245-7d38baf380d6',
      title: 'Bike',
      author: '@southside_customs',
    },
  ];

  export default Result;
